function Load(n){$("#divLoading"+n).show();Reset();$("#commentRow"+n).addClass("alert-success");$("#select"+n).hide();$("#hide"+n).show();$("#revisionDiv"+n).children().length==0&&$("#revisionDiv"+n).load("/index?handler=RevisionsPartial&commentHeaderId="+n,function(t,i,r){i=="error"&&$("#revisionDiv"+n).html("Sorry but there was an error: "+r.status+" "+r.statusText)});$("#divLoading"+n).hide();$("#revisionRow"+n).show()}function Reset(){$("[id^='commentRow']").removeClass("alert-success");$("[id^='hide']").hide();$("[id^='select']").show();$("[id^='revisionRow']").hide()}